            doc.text('_________________________________________________', 10, posY)
            posY += 8
            doc.text('ASSINATURA DO RESPONSAVEL', 10, posY)
            posY += 20
            doc.text('_________________________________________________', 10, posY)
            posY += 8
            doc.text('ASSINATURA DO DIRETOR PRESIDENTE', 10, posY)


            return doc;
        }
